mapper.select:
select 

from HXD_EVENT_CONSUMED
where EVENT_NAME = #{eventName,jdbcType=VARCHAR}
  and EVENT_KEY = #{eventKey,jdbcType=VARCHAR}
  and EVENT_CONSUMER_GROUP_ID = #{eventConsumerGroupId,jdbcType=VARCHAR}

mapper.delete:
delete from HXD_EVENT_CONSUMED
where EVENT_NAME = #{eventName,jdbcType=VARCHAR}
  and EVENT_KEY = #{eventKey,jdbcType=VARCHAR}
  and EVENT_CONSUMER_GROUP_ID = #{eventConsumerGroupId,jdbcType=VARCHAR}

mapper.update:
update HXD_EVENT_CONSUMED

  
    ID = #{id,jdbcType=BIGINT},
  
  
    EVENT_TIME = #{eventTime,jdbcType=TIMESTAMP},
  
  
    EVENT_DATA = #{eventData,jdbcType=VARCHAR},
  
  
    EVENT_CONSUME_TIME = #{eventConsumeTime,jdbcType=TIMESTAMP},
  

where EVENT_NAME = #{eventName,jdbcType=VARCHAR}
  and EVENT_KEY = #{eventKey,jdbcType=VARCHAR}
  and EVENT_CONSUMER_GROUP_ID = #{eventConsumerGroupId,jdbcType=VARCHAR}

update HXD_EVENT_CONSUMED
set ID = #{id,jdbcType=BIGINT},
  EVENT_TIME = #{eventTime,jdbcType=TIMESTAMP},
  EVENT_DATA = #{eventData,jdbcType=VARCHAR},
  EVENT_CONSUME_TIME = #{eventConsumeTime,jdbcType=TIMESTAMP}
where EVENT_NAME = #{eventName,jdbcType=VARCHAR}
  and EVENT_KEY = #{eventKey,jdbcType=VARCHAR}
  and EVENT_CONSUMER_GROUP_ID = #{eventConsumerGroupId,jdbcType=VARCHAR}
