  #{eventTime,jdbcType=TIMESTAMP}, #{eventData,jdbcType=VARCHAR}, #{eventConsumeTime,jdbcType=TIMESTAMP}
  )

mapper.update:
update HXD_EVENT_HISTORY
set ID = #{id,jdbcType=BIGINT},
  EVENT_TIME = #{eventTime,jdbcType=TIMESTAMP},
  EVENT_DATA = #{eventData,jdbcType=VARCHAR},
