mapper.insert:
insert into pay_accounting_detail (
  order_no, detail_type, detail_money, store_id, recorded_time, insert_time
) values (
  #{orderNo}, #{detailType}, #{detailMoney}, #{storeId}, #{recordedTime}, now()
)

mapper.select:
select d.store_id, d.verify_status, max(d.recorded_time) as last_recorded_time, h.operated_time, h.operated_by_name, sum(case when d.detail_type = 1 then d.detail_money else 0 end) as real_pay, sum(case when d.detail_type = 2 then d.detail_money else 0 end) as third_party_fee, sum(case when d.detail_type = 3 then d.detail_money else 0 end) as discount, sum(d.detail_money) as real_verify from pay_accounting_detail d left join pay_verify_history h on h.verify_code = d.verify_code where d.order_complete_status = 1 and d.third_party_verify_status = 1
and d.verify_status = #{verifyStatus}
group by d.store_id, d.verify_status, h.verify_code, h.operated_time, h.operated_by_name

mapper.update:
update pay_accounting_detail set
  order_complete_status = 1,
  recorded_time = #{recordedTime}
where order_no = #{orderNo}

mapper.insert:
insert into pay_verify_history (
  verify_status, verify_code, verify_remark, operated_time, operated_by, operated_by_name
) values (
  #{verifyStatus}, #{verifyCode}, #{verifyRemark}, now(), #{operatedBy}, #{operatedByName}
)
