mapper.select:
	 select oi.order_no, oi.store_id,sui.store_name,oi.region_code,
	 concat(sr.province,sr.city,sr.town,sr.county) as region_name,
	  oi.orderTotalMoney, oi.realPaymentMoney,oi.payType, oi.payStatus, oi.payFinishDateTime, 
 from order_info oi left join store_user_info sui on sui.id = oi.store_id
 left join sys_region sr on oi.region_code = sr.region_code
  where oi.payStatus = 1 and oi.orderStatus = 9
   
     and oi.order_no = #{orderNo,jdbcType=CHAR}
   
   
    and oi.store_id = #{storeId,jdbcType=BIGINT}
   
   
    and oi.region_code = #{regionCode,jdbcType=CHAR}
   
   
   
    AND oi.payFinishDateTime  >=  #{inCountStartTime}
   
   
    AND oi.payFinishDateTime  <=  #{inCountEndTime}

	 select 
  
  from pay_finance_account_detail 
  where status = 1 and type = 1
   
    and store_id = #{storeId,jdbcType=BIGINT}
   
   
    and region_code = #{regionCode,jdbcType=CHAR}
   
   
    and out_type = #{outType,jdbcType=SMALLINT}
   
   
    AND created  >=  #{createdStart}
   
   
    AND created  <=  #{createdEnd}
