mapper.select:
select

from pay_withdrawals
where 1 = 1

  and store_id = #{storeId}


  and audit_status = #{auditStatus}


  and callback_status = #{callbackStatus}


  and time_end >= date_format(#{withdrawalsDateStart}, '%Y-%m-%d')


  and time_end < date_add(date_format(#{withdrawalsDateEnd}, '%Y-%m-%d'), interval 1 day)


  and total_fee >= #{totalFeeStart}


  and total_fee <= #{totalFeeEnd}

order by 1 desc
