mapper.select:
select

from coupon_activity
where 1=1

  AND coupon_type = #{couponType,jdbcType=SMALLINT}


  AND status = #{status,jdbcType=SMALLINT}


  AND activity_status = #{activityStatus,jdbcType=SMALLINT}


  AND type = #{type,jdbcType=SMALLINT}

SELECT ct.STATUS, ct.reason, ct.pay_type payType, ct.cal_type calType, cg.type type, cgd.reduced_type reducedType, cgd.reduced_amt reducedAmt, cgd.discounted_amt discountedAmt, cgd.discounted discounted, cgd.discounted_max_amt discountedMaxAmt, cap.apply_rule_type applyRuleType, ca.coupon_type couponType, ca.activity_start activityStart, ca.activity_end activityEnd, CASE WHEN cts.status = 2 THEN '未使用' WHEN cts.STATUS = 1 THEN '已使用' WHEN cts.STATUS = 5 THEN '退回' WHEN cts.STATUS = 3 THEN '无效' WHEN cts.STATUS = 4 THEN '已过期' END AS useStatus FROM coupon_template_send cts JOIN coupon_template ct ON cts.template_id = ct.id JOIN coupon_activity_record car ON cts.id = car.send_id JOIN coupon_activity ca ON car.coupon_activity_id = ca.id JOIN coupon_grade cg ON ct.grade_id = cg.id JOIN coupon_grade_detail cgd ON cgd.grade_id = cg.id JOIN coupon_apply cap ON ct.apply_rule_id = cap.id WHERE cts.customer_id = #{customerId}
AND ca.coupon_type = #{couponType}
AND cts.STATUS = #{useStatus}
GROUP BY cts.template_id
