mapper.select:
SELECT ca.id, ca.`code`, ca.`name`, ( SELECT count(DISTINCT template_id) FROM coupon_activity_template WHERE coupon_activity_id = ca.id ) AS citeNum, ( SELECT count(DISTINCT store_id) FROM coupon_activity_store_customer WHERE coupon_activity_template_id = cat.id ) AS storeNum, cat.coupon_num_type, cat.coupon_num, ( SELECT count(cts.id) FROM coupon_template_send cts LEFT JOIN coupon_activity_template cat ON cts.template_id = cat.template_id WHERE cat.coupon_activity_id = ca.id ) AS getNum, ( SELECT count(cts.id) FROM coupon_template_send cts LEFT JOIN coupon_activity_template cat ON cts.template_id = cat.template_id WHERE cat.coupon_activity_id = ca.id AND cts. STATUS = '1' ) AS useNum, ( SELECT count(cts.id) FROM coupon_template_send cts LEFT JOIN coupon_activity_template cat ON cts.template_id = cat.template_id WHERE cat.coupon_activity_id = ca.id AND cts. STATUS = '0' ) AS revocationNum, ca.activity_start, ca.activity_end, cat.start_time AS coupon_start_time, cat.end_time AS coupon_end_time, ca.created_by_name, ca.created, ca.updated_by_name, ca.updated FROM coupon_activity ca LEFT JOIN coupon_activity_template cat ON cat.coupon_activity_id = ca.id LEFT JOIN coupon_template ct ON ct.id = cat.template_id LEFT JOIN coupon_activity_store_customer casc ON casc.coupon_activity_template_id = cat.id LEFT JOIN store_user_info sui ON sui.id = casc.store_id WHERE 1 = 1 AND ca.`status` = '1'
AND ca.`code` = #{condition.code}
AND ca.`name` = #{condition.name}
AND ct.`code` = #{condition.templateCode}
AND casc.store_id = #{condition.storeId}
AND cui.customer_mobile = #{condition.customerMobile}
AND sui.store_name = #{condition.storeName}
AND cat.coupon_num_type = #{condition.couponNumType}
AND cat.customer_voucher_limit_type = #{condition.customerVoucherLimitType}
AND ca.created_by_name = #{condition.createdByName}
AND ca.created < #{condition.createdStart}
AND ca.created > #{condition.createdEnd}

select

from coupon_activity
where 1=1

  AND coupon_type = #{couponType,jdbcType=SMALLINT}


  AND status = #{status,jdbcType=SMALLINT}


  AND activity_status = #{activityStatus,jdbcType=SMALLINT}


  AND type = #{type,jdbcType=SMALLINT}

SELECT ct.STATUS, ct.reason, ct.pay_type payType, ct.cal_type calType, cg.type type, cgd.reduced_type reducedType, cgd.reduced_amt reducedAmt, cgd.discounted_amt discountedAmt, cgd.discounted discounted, cgd.discounted_max_amt discountedMaxAmt, cap.apply_rule_type applyRuleType, cap.id applyId, ca.coupon_type couponType, ca.activity_start activityStart, ca.activity_end activityEnd, CASE WHEN cts.status = 2 THEN '未使用' WHEN cts.STATUS = 1 THEN '已使用' WHEN cts.STATUS = 4 THEN '退回' WHEN cts.STATUS = 0 THEN '无效' WHEN cts.STATUS = 3 THEN '已过期' END AS useStatus FROM coupon_template_send cts JOIN coupon_template ct ON cts.template_id = ct.id JOIN coupon_activity_record car ON cts.id = car.send_id JOIN coupon_activity ca ON car.coupon_activity_id = ca.id JOIN coupon_grade cg ON ct.grade_id = cg.id JOIN coupon_grade_detail cgd ON cgd.grade_id = cg.id JOIN coupon_apply cap ON ct.apply_rule_id = cap.id WHERE cts.customer_id = #{customerId} AND (cts.STATUS = 2 OR cts.STATUS = 4)
AND ca.coupon_type = #{couponType}
GROUP BY cts.template_id
