mapper.select:
SELECT DISTINCT ca.id, ca.`code`, ca.`name`, ca.`activity_status`, ( CASE WHEN ca.`activity_status` = 1 THEN '开启' WHEN ca.`activity_status` = 2 THEN '停用' END ) AS activity_status_name, cat.coupon_num_type, CASE WHEN cat.coupon_num_type = 1 THEN '优惠券总数' WHEN cat.coupon_num_type = 2 THEN '每个门店优惠券数' END AS coupon_num_type_name, cat.coupon_num, ca.activity_start, ca.activity_end, cat.start_time AS coupon_start_time, cat.end_time AS coupon_end_time, ca.created_by_name, ca.created, ca.updated_by_name AS numVALUE, ca.updated, count(DISTINCT casc.store_id) as storeNum, count(DISTINCT cat.template_id) as citeNum FROM coupon_activity ca LEFT JOIN coupon_activity_template cat ON cat.coupon_activity_id = ca.id AND cat.`status` = 1 LEFT JOIN coupon_template ct ON ct.id = cat.template_id AND ct.`status` = 1 LEFT JOIN coupon_activity_store_customer casc ON casc.coupon_activity_template_id = cat.id AND casc.`status` = 1 LEFT JOIN customer_user_info cui ON cui.customer_id = casc.customer_id AND cui.`status` = 1 LEFT JOIN store_user_info sui ON sui.id = casc.store_id AND sui.store_status = 1 WHERE 1 = 1 AND ca.`status` = 1
AND ca.`type` = '1'
AND ca.`type` = '2'
AND ca.`code` = #{condition.code}
AND ca.`name` = #{condition.name}
AND ct.`code` = #{condition.templateCode}
AND casc.store_id = #{condition.storeId}
AND cui.customer_mobile = #{condition.customerMobile}
AND sui.store_name = #{condition.storeName}
AND cat.coupon_num_type = #{condition.couponNumType}
AND cat.customer_voucher_limit_type = #{condition.customerVoucherLimitType}
AND ca.created_by_name = #{condition.createdByName}
AND ca.created >= #{condition.dateInterval.start,jdbcType=TIMESTAMP}
AND ca.created <= #{condition.dateInterval.end,jdbcType=TIMESTAMP}
GROUP BY ca.id ORDER BY ca.created DESC

select

from coupon_activity
where 1=1

  AND coupon_type = #{couponType,jdbcType=SMALLINT}


  AND status = #{status,jdbcType=SMALLINT}


  AND activity_status = #{activityStatus,jdbcType=SMALLINT}


  AND type = #{type,jdbcType=SMALLINT}

AND NOW() BETWEEN activity_start AND activity_end

SELECT cts.id sendId, ct.STATUS, ct.reason, ct.pay_type payType, ct.cal_type calType, cg.type type, cgd.reduced_type reducedType, cgd.reduced_amt reducedAmt, cgd.discounted_amt discountedAmt, (100-cgd.discounted)/10 discounted, cgd.discounted_max_amt discountedMaxAmt, cap.apply_rule_type applyRuleType, cap.id applyId, ca.coupon_type couponType, DATE_FORMAT(cts.start_time,'%Y.%m.%d') activityStart, DATE_FORMAT(cts.end_time,'%Y.%m.%d') activityEnd, CASE WHEN cts.status = 2 THEN '未使用' WHEN cts.STATUS = 1 THEN '已使用' WHEN cts.STATUS = 4 THEN '退回' WHEN cts.STATUS = 0 THEN '无效' WHEN cts.STATUS = 3 THEN '已过期' END AS useStatus FROM coupon_template_send cts JOIN coupon_template ct ON cts.template_id = ct.id JOIN coupon_activity_record car ON cts.id = car.send_id JOIN coupon_activity ca ON car.coupon_activity_id = ca.id JOIN coupon_grade cg ON ct.grade_id = cg.id JOIN coupon_grade_detail cgd ON cgd.grade_id = cg.id JOIN coupon_apply cap ON ct.apply_rule_id = cap.id WHERE cts.customer_id = #{customerId}
AND ca.coupon_type = #{couponType}
AND cts.status = #{status}
AND cts.end_time < now()
AND cts.start_time <= NOW() AND cts.end_time >= NOW() AND (cts.STATUS = 2 OR cts.STATUS = 4)

SELECT cts.id sendId, ct.STATUS, ct.reason, ct.pay_type payType, ct.cal_type calType, cg.type type, cgd.reduced_type reducedType, cgd.reduced_amt reducedAmt, cgd.discounted_amt discountedAmt, (100-cgd.discounted)/10 discounted, cgd.discounted_max_amt discountedMaxAmt, cap.apply_rule_type applyRuleType, cap.id applyId, DATE(cts.start_time) activityStart, DATE(cts.end_time) activityEnd, CASE WHEN cts.status = 2 THEN '未使用' WHEN cts.STATUS = 1 THEN '已使用' WHEN cts.STATUS = 4 THEN '退回' WHEN cts.STATUS = 0 THEN '无效' WHEN cts.STATUS = 3 THEN '已过期' END AS useStatus FROM coupon_template_send cts JOIN coupon_template ct ON cts.template_id = ct.id JOIN coupon_grade cg ON ct.grade_id = cg.id JOIN coupon_grade_detail cgd ON cgd.grade_id = cg.id JOIN coupon_apply cap ON ct.apply_rule_id = cap.id WHERE cts.customer_id = #{customerId}
AND ca.coupon_type = #{couponType}
AND cts.status = #{status}
AND cts.end_time < now()
AND cts.start_time <= NOW() AND cts.end_time >= NOW() AND (cts.STATUS = 2 OR cts.STATUS = 4)
GROUP BY cts.template_id

SELECT cts.id sendId, count(*) couponNum, ct.STATUS, ct.reason, ct.pay_type payType, ct.cal_type calType, cg.type type, cgd.reduced_type reducedType, cgd.reduced_amt reducedAmt, cgd.discounted_amt discountedAmt, (100-cgd.discounted)/10 discounted, cgd.discounted_max_amt discountedMaxAmt, cap.apply_rule_type applyRuleType, cap.id applyId, ca.coupon_type couponType, DATE(cts.start_time) activityStart, DATE(cts.end_time) activityEnd, CASE WHEN cts.status = 2 THEN '未使用' WHEN cts.STATUS = 1 THEN '已使用' WHEN cts.STATUS = 4 THEN '退回' WHEN cts.STATUS = 0 THEN '无效' WHEN cts.STATUS = 3 THEN '已过期' END AS useStatus FROM coupon_template_send cts JOIN coupon_template ct ON cts.template_id = ct.id JOIN coupon_activity_record car ON cts.id = car.send_id JOIN coupon_activity ca ON car.coupon_activity_id = ca.id JOIN coupon_grade cg ON ct.grade_id = cg.id JOIN coupon_grade_detail cgd ON cgd.grade_id = cg.id JOIN coupon_apply cap ON ct.apply_rule_id = cap.id WHERE cts.customer_id = #{customerId} AND cts.start_time <= NOW() AND cts.end_time >= NOW() AND (cts.STATUS = 2 OR cts.STATUS = 4)
AND ca.coupon_type = #{couponType}
GROUP BY ca.id,cts.template_id

