set customer_id = #{customerId,jdbcType=BIGINT},
  coupon_activity_id = #{couponActivityId,jdbcType=BIGINT},
  receive = #{receive,jdbcType=BIT},
  temp = #{temp,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
