mapper.select:
select

from coupon_template a
left join coupon_investor b on a.investor_id = b.id
left join coupon_grade c on a.grade_id = c.id
left join coupon_apply d on a.apply_rule_id = d.id
where 1=1

  and a.created  >=  #{condition.createdBegin}


  and a.created  <=  #{condition.createdEnd}


  and a.created_by_name = #{condition.createdByName}


  and a.status = #{condition.status}


   and a.code = #{condition.code}


   and a.title = #{condition.title}


  and a.coupon_label = #{condition.couponLabel}


  and a.cal_type = #{condition.cal_type}


  and b.investor_name = #{condition.investorName}


  and c.grade_name = #{condition.gradeName}


  and d.apply_rule_name = #{condition.applyRuleName}



  and d.apply_rule_type = #{condition.applyRuleType}

mapper.update:
update coupon_template set status= 0 ,updated_by=#{updatedBy},updated_by_name=#{updateByName},updated=#{updated}
where id in

 #{item}
