mapper.insert:
insert into store_region (id, area_name, area_code, 
  level, status, updated_by, 
  created_by, created, updated
  )
values (#{id,jdbcType=BIGINT}, #{areaName,jdbcType=VARCHAR}, #{areaCode,jdbcType=VARCHAR}, 
  #{level,jdbcType=SMALLINT}, #{status,jdbcType=SMALLINT}, #{updatedBy,jdbcType=BIGINT}, 
  #{createdBy,jdbcType=BIGINT}, #{created,jdbcType=TIMESTAMP}, #{updated,jdbcType=TIMESTAMP}
  )

mapper.select:
select  from store_region
where 1 = 1

  and area_name = #{areaName, jdbc=VARCHAR}


  and level = #{level, jdbc=SMALLINT}

and status = 0
order by created desc
