mapper.insert:
insert into sys_user (id, account, username,
  mobile, status, password,
  created_by, created_by_name, created,
  updated_by, updated, updated_by_name
  )
values (#{id,jdbcType=BIGINT}, #{account,jdbcType=VARCHAR}, #{username,jdbcType=VARCHAR},
  #{mobile,jdbcType=CHAR}, #{status,jdbcType=SMALLINT}, #{password,jdbcType=VARCHAR},
  #{createdBy,jdbcType=BIGINT}, #{createdByName,jdbcType=VARCHAR}, #{created,jdbcType=TIMESTAMP},
  #{updatedBy,jdbcType=BIGINT}, #{updated,jdbcType=TIMESTAMP}, #{updatedByName,jdbcType=VARCHAR}
  )

mapper.select:
select u.id, u.account, u.username, u.mobile, u.status, u.created_by, u.created, u.updated_by, u.updated, u.created_by_name, u.updated_by_name, ur.id, ur.role_id, r.role_name, u.identity from sys_user u left join sys_user_role ur on u.id=ur.user_id left join sys_role r on r.id=ur.role_id
u.status=1
and u.id = #{userId}
and u.account = #{account}
and username like #{username,jdbcType=VARCHAR}
and mobile like #{mobile,jdbcType=CHAR}
and role_name like #{roleName,jdbcType=VARCHAR}
