mapper.select:
select u.id, u.account, u.username, u.mobile, u.status, u.created_by, u.created, u.updated_by, u.updated, u.created_by_name, u.updated_by_name, ur.role_id, r.role_name, u.identity, u.region_code, CONCAT(re.province, re.city, re.county, re.town, re.village) AS region_name FROM sys_user u LEFT JOIN sys_user_role ur ON u.id = ur.user_id LEFT JOIN sys_role r ON r.id = ur.role_id LEFT JOIN sys_region re ON u.region_code = re.region_code
u.status=1
and u.id = #{userId}
and u.account = #{account}
and username like #{username,jdbcType=VARCHAR}
and mobile like #{mobile,jdbcType=CHAR}
and role_id = #{roleId}
