mapper.select:
select
u.id, account, user_name, mobile, u.status, u.created_by, u.created, u.updated_by,
u.updated,ur.id rule_id,r.rule_name
from sys_user u
  left join sys_user_rule ur on u.id=ur.user_id
  left join sys_rule r on r.id=ur.rule_id


    and u.id = #{userId}


    and u.account = #{account}



    and user_name like #{userName,jdbcType=VARCHAR}



    and mobile like #{mobile,jdbcType=CHAR}



    and rule_name like #{ruleName,jdbcType=VARCHAR}
